import { NextApiRequest, NextApiResponse } from "next";
import { createApi } from "unsplash-js";

interface BackgroundResponse {
  status?: number;
  urls: {
    raw: string;
    full: string;
    regular: string;
    small: string;
    thumb: string;
  };
}

async function Background(req: NextApiRequest, res: NextApiResponse) {
  const unsplash = createApi({
    accessKey: "DAQrUWKCxhZMjDH2S_X0sFv27A3OO0X3usud_ryy2CM",
  });

  let q = await unsplash.search.getPhotos({
    query: "cat",
  });

  let resCount = 0;
  resCount += 1;
  const response: BackgroundResponse = {
    status: q.status,
    urls: q.response.results.urls,
  };

  res.status(q.status).json(response);
}

export default Background;
