import styled from "@emotion/styled";

const TaskBarContainer = styled.header`
  display: grid;
  justify-content: space-between;
  height: 35px;
  position: sticky;
  position: -webkit-sticky;
  top: 0;
  z-index: auto;
  background-color: var(--bg-two);
  overflow: hidden;

  * {
    padding: 0;
    margin: 0;
  }
`;

export { TaskBarContainer };
