import styled from "styled-components";

const WorkspaceButtonContainer = styled.div`
  // height: 100%;
  // width: 1.5rem;
  color: var(--font-color);
  border-bottom: 3px solid var(--${(props) => props.color || "color-blue"});
  align-self: center;
  text-align: center;
  background-color: var(
    --${(props: any) => (props.activate ? "bg-four" : "bg-two")}
  );
`;

export { WorkspaceButtonContainer };
