import styled from "styled-components";

interface StyleProps {
  activate?: boolean;
}

const WorkspaceButtonContainer = styled.div`
  display: grid;
  height: 100%;
  width: 1.5rem;
  color: var(--font-color);
  text-align: center;
  align-items: center;
  background-color: var(
    --${(props: StyleProps) => (props.activate ? "bg-four" : "bg-one")}
  );
`;

export { WorkspaceButtonContainer };
