import styled from "styled-components";

const WorkspaceButtonContainer = styled.div`
  height: 100%;
  width: 1.5rem;
  position: absolute;
  padding: auto;
  color: var(--font-color);
  border-bottom: 6px solid var(--${(props) => props.color || "color-blue"});
  align-self: center;
  text-align: center;
  background-color: var(
    --${(props: any) => (props.activate ? "bg-four" : "bg-one")}
  );

  span {
    position: relative;
    top: 50%;
    line-height: 100%;
  }
`;

export { WorkspaceButtonContainer };
